import axios from "axios";
import { useQuery } from "react-query";
import { toast } from "react-toastify";

export interface AllProduct {
  id: number;
  title: string;
  price: string;
  category: string;
  description: string;
  image: string;
}

export const GetAllProduct = (selectedId:number) => {
  return axios.get(`https://fakestoreapi.com/products/${selectedId}`).then((res) => res.data);
};

export const useAllProduct = (selectedId: number | undefined) => {
  toast.success("Failed to Product");
  return useQuery<AllProduct[]>(
    `https://fakestoreapi.com/products/${selectedId}`,
    () => GetAllProduct(selectedId as number),
    {
      onError: (err: any) => {
        "Failed to Product";
      },
      enabled: Boolean(selectedId !== undefined),
      refetchOnMount: false,
      retry: 2,
    }
  );
};
