import { useState } from "react";
import { useAllProduct } from "../Api/GetApi";
import { CartIcon } from "../svg";
import "./Home.scss";
import { Banner } from "../Banner/Banner";

export const HomePage = () => {
  const [selectedId, setSelectedId] = useState<number | undefined>(undefined);
  const { data, isLoading } = useAllProduct(selectedId);
  return (
    <div className="home-page">
      <div className="title-block">
        <h3 className="title">Latest Arrivals </h3>
        <div className="border-div"></div>
      </div>

      {isLoading ? (
        <>loading...</>
      ) : (
        <div className="row">
          {Array.isArray(data) ? (
            data?.map((item, index) => {
              return (
                <div className="col-md-3" key={index}>
                  <div className="product-list">
                    <div className="product-section">
                      <div className="product-grid ">
                        <img
                          src={item.image}
                          alt="img "
                          className="product-img"
                          onDoubleClick={() => {
                            setSelectedId(item.id);
                            //   alert(item.id);
                          }}
                        />
                      </div>
                      <div className="product-title">{item.title}</div>
                      <div className="price-cart-btn-section">
                        <label className="price">RS {item.price}</label>
                        <div className="card-btn ">
                          <CartIcon />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="produt-section">
              <p className="category-content">{data?.category}</p>
              <div className="produt-wrapper">
                <div className="width-50">
                  <div className="product-list">
                    <div className="product-section">
                      <div className="product-grid ">
                        <img
                          src={data?.image}
                          alt="img "
                          className="product-img"
                        />
                      </div>
                    </div>
                  </div>
                </div>
                <div className="width-50">
                  <div className="product-title">{data?.title}</div>
                  <label className="price">RS {data?.price}</label>
                  <div className="product-description">
                    <p>Product Description</p>
                    <p>{data?.description}</p>
                  </div>
                  <div>
                    <button className="main-btn">Add To Cart</button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      )}

      {/* <div className="container">
    <div className="row">
      <div className="col-md-3">
        <div className="product-list">
          <div className="product-section">
            <div className="product-grid "></div>
            <div className="product-title">product-title</div>
            <div className="price-cart-btn-section">
              <label className="price">RS 1000</label>
              <div className="card-btn ">CD</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div> */}
    </div>
  );
};
